      .then(data => {
        commit('deleteAllCompleted')
        notify({
          content: '删除已完成TODOs'
        })
      })
      .catch(err => {
        handleErr(err)
      })
  },
  login ({
    commit
  }, {
    username,
    password
  }) {
    return new Promise((resolve, reject) => {
      model.login(username, password)
        .then(data => {
          commit('doLogin', data)
          notify({
            content: '登陆成功'
          })
          resolve()
        })
        .catch(err => {
          handleErr(err)
          reject(err)
        })
    })
  }
}

